//ABが同時に押されたとき
input.onButtonPressed(Button.AB, function () {
    let bullet = game.createSprite(gun.x(), gun.y())//弾を生成
    bullet.turn(Direction.Left, 90)//球の進行方向を上向きに回転
    while (bullet.y()) {//弾が0より大きい間ずっと
        bullet.move(1)//上に移動
        basic.pause(100)//一時停止
    }
    if (bullet.isTouching(enemy)) {//弾が敵に接していたら
        enemy.delete()//敵を消去
        basic.pause(100)
        basic.showIcon(IconNames.Happy, 2000)//クリア画面
        control.reset()
    }
    bullet.delete()//弾を消去
})
